mapper.update:
Update IT_Category 
set postTitle = #{postTitle},
    postCategory = #{postCategory},
    postDate = #{postDate},
    contents = #{contents}
    where postNo = #{postNo}

Update IT_Category
set commentsCount =#{commentsCount}
where postNo =#{postNo}

