mapper.update:
update IT_Comments set
	comments = #{comments},
	postDate = #{postDate}
where commentsNo = #{commentsNo}
	and postNo = #{postNo}

mapper.delete:
delete from IT_Comments
where commentsNo = #{commentsNo}
	and postNo = ${postNo}

